using System;
using System.Threading;
using System.Threading.Tasks;
using Acutis.Application.Services;  namespace Acutis.Functions;  /// <summary> /// Placeholder façade for future Azure Functions integration. The actual HTTP trigger attributes are /// intentionally omitted because the Azure Functions runtime packages are not referenced in this prototype. /// </summary> public class TherapyFunctionsFacade {     private readonly ITherapyCatalogService _catalogService;      public TherapyFunctionsFacade(ITherapyCatalogService catalogService)     {         _catalogService = catalogService;     }      /// <summary>     /// Mocked entry point that would be exposed as an Azure Function HTTP trigger once the runtime is wired.     /// </summary>     public async Task<object> GetTherapyTermsAsync(CancellationToken cancellationToken = default)     {         var ratings = await _catalogService.GetRatingsAsync(cancellationToken);         return new         {             GeneratedAt = DateTimeOffset.UtcNow,             Ratings = ratings         };     } }
